$(document).ready(function(){$("#url").keyup(function(a){if(a.keyCode==13){search()}});$(".buscar").click(function(){search()});$(".result img, .dl").click(function(){window.open("php/downloadPhoto.php?url_img="+$(".result img").prop("src"),"_blank")})});function search(){if(isValidUrl($("#url").val(),1,0)){$.ajax({type:"post",url:"php/getHtml.php",dataType:"json",data:{url:$("#url").val()},beforeSend:function(){$(".result").hide().children("img").prop("src","");$(".alert").hide();$(".loading").removeClass("hide")},success:function(a){if(a.photo.indexOf("https")>-1){$(".alert").hide();$(".loading").addClass("hide");$(".header").animate({"margin-top":"63px"},300,function(){$(".result header").html(a.username).next().prop("src",a.photo).parent().show();$("html, body").animate({scrollTop:$(".result").offset().top},0);animate(".result","zoomInUp")})}else{showError()}},error:function(){showError()},timeout:100000})}else{showError()}}function showError(){$(".result").hide();$(".alert").fadeIn();$(".loading").addClass("hide");$(".header").animate({"margin-top":"20%"},300);animate("#url, .buscar","shake");setTimeout(function(){$(".alert").fadeOut()},5000)}function animate(b,a){$(b).removeClass(a+" animated").addClass(a+" animated").one("webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",function(){$(b).removeClass(a+" animated")})}function isValidUrl(a,d,c){if(d==undefined){d=0}if(c==undefined){c=0}if(a==""&&d==0){return true}if(c){var b=/^(http|https|ftp)\:\/\/[a-z0-9\.-]+\.[a-z]{2,4}/gi}else{var b=/^(http|https)\:\/\/[a-z0-9\.-]+\.[a-z]{2,4}/gi}if(a.match(b)){return true}else{return false}};
